import "core-js/modules/es.array.push.js";
import axios from 'axios';
import { getWaveQuality } from '../utils/waveGrading.js';
import VueApexCharts from 'vue3-apexcharts';
export default {
  components: {
    ApexChart: VueApexCharts
  },
  props: {
    beach: String,
    beachAngle: Number
  },
  data() {
    return {
      waveData: [],
      windData: [],
      chartSeries: [],
      chartOptions: {},
      isDesktop: true,
      chartHeight: '520px',
      chartMinWidth: '1000px' // Minimum width for horizontal scrolling
    };
  },
  computed: {
    chartHeightNumeric() {
      return parseInt(this.chartHeight.replace('px', ''), 10);
    }
  },
  mounted() {
    this.checkViewport();
    window.addEventListener('resize', this.checkViewport);
    this.fetchData();
  },
  beforeUnmount() {
    window.removeEventListener('resize', this.checkViewport);
  },
  methods: {
    checkViewport() {
      const width = window.innerWidth;
      this.isDesktop = width > 768; // Adjust breakpoint for desktop/mobile
      this.chartHeight = this.isDesktop ? '520px' : '350px';
      this.chartMinWidth = this.isDesktop ? '1000px' : '600px'; // Adjust minimum width for scrolling
    },
    async fetchData() {
      try {
        const waveResponse = await axios.get(`https://3k7dz8sjwk.execute-api.us-east-2.amazonaws.com/TestStage/waves/${this.beach}`);
        const windResponse = await axios.get(`https://3k7dz8sjwk.execute-api.us-east-2.amazonaws.com/TestStage/wind/${this.beach}`);
        const waveData = JSON.parse(waveResponse.data.waves);
        const windData = JSON.parse(windResponse.data.wind);
        this.waveData = waveData.hourly;
        this.windData = windData.hourly;
        this.setupChart();
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    },
    setupChart() {
      const waveHeights = this.waveData.wave_height;
      const colors = waveHeights.map((height, index) => {
        const windSpeed = this.windData.wind_speed_10m[index];
        const windDirection = this.windData.wind_direction_10m[index];
        const wavePeriod = this.waveData.wave_period[index];
        const beachAngle = this.beachAngle || 120;
        const waveGrade = getWaveQuality(windDirection, windSpeed, wavePeriod, beachAngle, height);
        return waveGrade === 'green' ? '#2ecc71' : waveGrade === 'yellow' ? '#f1c40f' : '#e74c3c';
      });
      const labels = this.waveData.time.map(time => {
        const date = new Date(time);
        return date.toLocaleTimeString('en-US', {
          hour: '2-digit',
          minute: '2-digit'
        });
      });
      const days = [];
      for (let i = 0; i < labels.length; i += 8) {
        const date = new Date(this.waveData.time[i]);
        const dayLabel = date.toLocaleDateString('en-US', {
          weekday: 'short',
          month: 'short',
          day: 'numeric'
        });
        days.push({
          title: dayLabel,
          cols: 8
        });
      }
      this.chartSeries = [{
        name: 'Wave Height (ft)',
        data: waveHeights
      }];
      this.chartOptions = {
        chart: {
          type: 'bar'
        },
        plotOptions: {
          bar: {
            borderRadius: 4,
            distributed: true
          }
        },
        xaxis: {
          categories: labels,
          tickPlacement: 'on',
          group: {
            style: {
              fontSize: '10px',
              fontWeight: 700
            },
            groups: days // Set up day groupings on the x-axis
          }
        },
        colors: colors,
        tooltip: {
          custom: ({
            dataPointIndex
          }) => {
            const waveHeight = this.waveData.wave_height[dataPointIndex];
            const wavePeriod = this.waveData.wave_period[dataPointIndex];
            const windSpeed = this.windData.wind_speed_10m[dataPointIndex];
            const windDirectionDegrees = this.windData.wind_direction_10m[dataPointIndex];
            const windDirectionCardinal = this.convertDegreesToCardinal(windDirectionDegrees);
            const date = new Date(this.waveData.time[dataPointIndex]);
            const dayLabel = date.toLocaleDateString('en-US', {
              weekday: 'short',
              month: 'short',
              day: 'numeric'
            });
            const timeLabel = date.toLocaleTimeString('en-US', {
              hour: '2-digit',
              minute: '2-digit'
            });
            return `<div style="padding:8px; border-radius: 4px; background: #fff;">
              <div style="font-weight: bold; font-size: 14px; margin-bottom: 6px;">
                ${dayLabel}, ${timeLabel}
              </div>
              <strong>Height:</strong> ${waveHeight} ft<br>
              <strong>Period:</strong> ${wavePeriod} s<br>
              <strong>Wind Speed:</strong> ${windSpeed} kn<br>
              <strong>Wind Direction:</strong> ${windDirectionCardinal} (${windDirectionDegrees}Â°)
            </div>`;
          }
        },
        legend: {
          show: false // Removes the legend
        },
        dataLabels: {
          enabled: false // Removes labels on bars
        }
      };
    },
    convertDegreesToCardinal(degrees) {
      const directions = ['N', 'NNE', 'NE', 'ENE', 'E', 'ESE', 'SE', 'SSE', 'S', 'SSW', 'SW', 'WSW', 'W', 'WNW', 'NW', 'NNW'];
      const index = Math.round(degrees % 360 / 22.5);
      return directions[index];
    }
  }
};
