import { Chart } from 'chart.js';
import axios from 'axios';
import { getWaveQuality } from '../utils/waveGrading.js'; // Import the grading function

export default {
  props: {
    beach: String,
    // Beach name passed from the parent component
    beachAngle: Number //Beach angle passed from the parent component
  },
  data() {
    return {
      waveData: [],
      // Array to store wave data
      windData: [],
      // Array to store wind data
      hoveredWave: null // Stores data for the hovered wave
    };
  },
  mounted() {
    this.fetchData();
  },
  methods: {
    // Fetch wave and wind data from the API
    async fetchData() {
      try {
        const waveResponse = await axios.get(`https://api-url/waves/${this.beach}`);
        const windResponse = await axios.get(`https://api-url/wind/${this.beach}`);
        // Continue with successful fetch
        this.buildChart();
      } catch (error) {
        console.error('Error fetching data:', error);
        // Provide default or fallback data to render something
        this.waveData = {
          hourly: {
            wave_height: [],
            wave_period: []
          }
        };
        this.windData = {
          hourly: {
            wind_speed_10m: [],
            wind_direction_10m: []
          }
        };
        this.buildChart;
      }
    },
    // Build the bar chart using Chart.js
    buildChart() {
      const ctx = this.$refs.waveChart.getContext('2d');

      // Prepare the datasets for the chart
      const waveHeights = this.waveData.wave_height;
      const labels = this.waveData.time.map(time => new Date(time).toLocaleDateString());

      // Apply the grading logic using getWaveQuality
      const barColors = waveHeights.map((height, index) => {
        const windSpeed = this.windData.wind_speed_10m[index];
        const windDirection = this.windData.wind_direction_10m[index];
        const wavePeriod = this.waveData.wave_period[index];
        const beachAngle = this.beachAngle || 120; // Use actual beach angle data if available

        // Get the wave quality using the utility function
        const waveGrade = getWaveQuality(windDirection, windSpeed, wavePeriod, beachAngle, height);

        // Map the grade to a color
        return waveGrade === 'green' ? 'green' : waveGrade === 'yellow' ? 'yellow' : 'red'; // Default to red for poor waves
      });
      new Chart(ctx, {
        type: 'bar',
        data: {
          labels: labels,
          datasets: [{
            label: 'Wave Height (ft)',
            data: waveHeights,
            backgroundColor: barColors // Use calculated colors based on grading
          }]
        },
        options: {
          responsive: true,
          tooltips: {
            callbacks: {
              label: tooltipItem => {
                const index = tooltipItem.index;
                const waveHeight = this.waveData.wave_height[index];
                const wavePeriod = this.waveData.wave_period[index];
                const windSpeed = this.windData.wind_speed_10m[index];
                const windDirection = this.windData.wind_direction_10m[index];

                // Display the detailed wave info in the tooltip
                this.hoveredWave = {
                  height: waveHeight,
                  period: wavePeriod,
                  wind_speed: windSpeed,
                  wind_direction: windDirection
                };
                return `Height: ${waveHeight} ft, Period: ${wavePeriod} s, Wind: ${windSpeed} kn, Dir: ${windDirection}°`;
              }
            }
          }
        }
      });
    }
  }
};
